
public class Swap_Two_Variable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String x = "water";
		String y = "Kool-Aid";
		String temp;
		temp = x;//temp=water
		x=y;//x=kool-Aid
		y=temp;//y=water
		
		System.out.println("x: "+x);
		System.out.println("y: "+y);

	}

}
